# PART ONE
fours = []
with open('day6input.txt') as f:
    data = f.read()
    for char in data:
        fours.append(char)
        if len(fours) > 4:
            set_of_four = set(fours[-5:-1])
            if len(set_of_four) == 4:
                print(len(fours) - 1)
                print(set_of_four)
                break

